mapper.update:
UPDATE member
SET name = #{name},
    phone = #{phone}
WHERE email = #{userId}

UPDATE member
SET status = 'SUSPENDED'
WHERE email = #{userId}

UPDATE member
SET status = 'ACTIVE'
WHERE email = #{userId}

UPDATE member
SET status = #{status}
WHERE email = #{userId}

UPDATE member
SET role = #{role}
WHERE email = #{userId}

UPDATE volunteer_applications
SET
    status = 'REJECTED',
    reject_reason = #{reason}
WHERE application_id = #{applicationId}
