mapper.select:
SELECT COUNT(*)
FROM password_history
WHERE member_id = #{memberId}
AND password_hash = #{passwordHash}

mapper.delete:
DELETE FROM password_history
WHERE member_id = #{memberId}
AND history_id NOT IN (
    SELECT history_id FROM (
        SELECT history_id
        FROM password_history
        WHERE member_id = #{memberId}
        ORDER BY created_at DESC
        LIMIT 5
    ) AS recent
)
